import styles from './Button.module.css'
import arrow from './../assets/arrow-right-solid.png'
import trash from './../assets/trash.png'

type Props = {
  title?: string
  color: 'gray' | 'red'
  intensity: number
  size: 's' | 'm' | 'lg'
}

export const Button = ({
  title,
  color,
  intensity,
  size,
}: Props) => {

  const buttonSize = size === 's' ? styles.sSizeButton :
    size === 'm' ? styles.mSizeButton :
    size === 'lg' ? styles.lgSizeButton : {}

  const buttonStyles = styles.button

  return (
    <button
      className={`bg-${color}-${intensity} ${buttonSize} ${buttonStyles} rounded-lg text-white font-[600]`}
    >
      {title}
    </button>
  )
}
